Full dataset edits

wine_final &lt;- wine_data_full |&gt;
                filter(quality &gt; 6.5 & alcohol &lt; 132) |&gt;
                arrange(desc(quality)) |&gt;
                select(contains("acid"), alcohol, type, quality) |&gt;
                mutate(
                  across(alcohol, mean, .names = "mean_{.col}"),
                  across(alcohol, sd, .names = "sd_{.col}")
                ) |&gt;
                group_by(quality)
